'  RETURN apex_plugin.t_page_item_render_result IS',
'  -- custom plugin attributes',
'  l_result  apex_plugin.t_page_item_render_result;',
'  l_attr_01 p_item.attribute_01%TYPE := p_item.attribute_01;',
'  l_attr_02 p_item.attribute_02%TYPE := p_item.attribute_02;',
'  l_attr_03 p_item.attribute_03%TYPE := p_item.attribute_03;',
'  l_attr_04 p_item.attribute_04%TYPE := p_item.attribute_04;',
'  l_attr_05 p_item.attribute_05%TYPE := p_item.attribute_05;',
'  l_attr_06 p_item.attribute_06%TYPE := p_item.attribute_06;',
'  l_attr_07 p_item.attribute_07%TYPE := p_item.attribute_07;',
'  l_attr_08 p_item.attribute_08%TYPE := p_item.attribute_08;',
'  ',
'  -- other vars',
'  l_autoplay        BOOLEAN := false;',
'  l_show_arrows     BOOLEAN := false;',
'  l_show_buttons    BOOLEAN := false;',
'  ',
'  --',
'  l_name            VARCHAR2(30);',
'  l_escaped_value   VARCHAR2(4000);',
'  l_html_string     VARCHAR2(2000);',
'  l_element_item_id VARCHAR2(200);',
'  ',
'  --',
'  l_code            VARCHAR2(4000);',
'  l_column_value_list   apex_plugin_util.t_column_value_list;',
'BEGIN',
'  IF l_attr_04 = ''Y'' THEN',
'    l_autoplay := true;',
'  END IF;',
'',
'  IF l_attr_05 = ''Y'' THEN',
'    l_show_arrows := true;',
'  END IF;',
'',
'  IF l_attr_06 = ''Y'' THEN',
'    l_show_buttons := true;',
'  END IF;',
'  ',
'  -- Normal Display',
'  l_element_item_id := p_item.name;',
'  l_name            := apex_plugin.get_input_name_for_page_item(FALSE);',
'  l_escaped_value   := apex_escape.html(p_value);',
'  --',
'  -- build input html string',
'  l_html_string := ''<div class="slider-pro" id="'' || l_element_item_id || ''"'';',
'  l_html_string := l_html_string || '' '' || p_item.element_attributes || ''">'';',
'',
'  l_html_string := l_html_string || ''<div class="sp-slides">'';',
'',
'  l_column_value_list :=',
'      apex_plugin_util.get_data (',
'          p_sql_statement    => p_item.lov_definition,',
'          p_min_columns      => 1,',
'          p_max_columns      => 2,',
'          p_component_name   => p_item.name);',
'',
'  for i in 1 .. l_column_value_list(1).count',
'  loop',
'    l_html_string := l_html_string || ''<!-- Slide '' || i || '' -->'';',
'    l_html_string := l_html_string || ''<div class="sp-slide">'';',
'    l_html_string := l_html_string || ''<img '';',
'    l_html_string := l_html_string || ''class="sp-image" '';',
'    l_html_string := l_html_string || ''src="'' || sys.htf.escape_sc(l_column_value_list(1)(i)) || ''" ''; ',
'    l_html_string := l_html_string || ''alt="'' || sys.htf.escape_sc(l_column_value_list(2)(i)) || ''" '';   ',
'    l_html_string := l_html_string || ''/>'';',
'    l_html_string := l_html_string || ''</div>'';         ',
'  end loop;',
'',
'  l_html_string := l_html_string || ''</div></div>'';',
'',
'  --',
'  -- add JavaScript files',
'   apex_javascript.add_library(p_name           => ''jquery.sliderPro.min'',',
'                              p_directory      => p_plugin.file_prefix || ''js/'',',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'  --',
'  -- add CSS files',
'  apex_css.add_file(p_name      => ''slider-pro.min'',',
'                    p_directory => p_plugin.file_prefix || ''css/'',',
'                    p_skip_extension => FALSE);',
'',
'  --',
'  -- add Onload Code   ',
'  l_code := ''$("#'' || l_element_item_id || ''").sliderPro({'' ||',
'            apex_javascript.add_attribute(''orientation'',l_attr_01) ||',
'            apex_javascript.add_attribute(''width'',l_attr_02) ||',
'            apex_javascript.add_attribute(''height'',l_attr_03) ||',
'            apex_javascript.add_attribute(''autoplay'',l_autoplay) ||',
'            apex_javascript.add_attribute(''arrows'',l_show_arrows) ||',
'            apex_javascript.add_attribute(''buttons'',l_show_buttons) ||',
'            l_attr_07 ||',
'            ''breakpoints: {'' || l_attr_08 || ''}'' || ',
'            ''});'';',
'',
'  apex_javascript.add_onload_code(p_code => l_code);',
'',
'  -- write item html',
'  htp.p(l_html_string);',
'  --',
'  l_result.is_navigable := TRUE;',
'  RETURN l_result;',
'  --',
'END render_item;'))
